// src/components/ViewIssueModal.js
import React from 'react';

export default function ViewIssueModal({ issue, onClose }) {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-3xl overflow-y-auto max-h-[90vh]">
        <h2 className="text-xl font-bold mb-4">Issue Details</h2>

        <div className="mb-3">
          <strong>Title:</strong> {issue.issueTitle}
        </div>
        <div className="mb-3">
          <strong>Application:</strong> {issue.application}
        </div>
        <div className="mb-3">
          <strong>Description:</strong>
          <div className="text-gray-700 whitespace-pre-wrap">{issue.description}</div>
        </div>

        {issue.solution ? (
          <div className="mt-6 border-t pt-4">
            <h3 className="text-lg font-semibold text-green-700">Solution Details</h3>
            <div className="mb-2">
              <strong>Type:</strong> {issue.solution.category}
            </div>
            <div className="mb-2">
              <strong>Description:</strong>
              <div className="text-gray-700 whitespace-pre-wrap">{issue.solution.description}</div>
            </div>
            <div className="mb-2">
              <strong>Solved By:</strong> {issue.solution.solvedBy?.ntId} ({issue.solution.solvedBy?.email})
            </div>
          </div>
        ) : (
          <div className="mt-6 text-yellow-600 italic">No solution submitted yet.</div>
        )}

        <div className="mt-6 text-right">
          <button
            onClick={onClose}
            className="px-4 py-2 bg-gray-300 text-gray-800 rounded hover:bg-gray-400"
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
}
