import React, { useState } from 'react';
import { Search, Download } from 'lucide-react';
import JSZip from 'jszip';
import { saveAs } from 'file-saver';
import axios from 'axios';
import IssueCard from '../components/IssueCard';

export default function Implementation() {
  const [searchQuery, setSearchQuery] = useState('');
  const [issues, setIssues] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`/api/issues?search=${searchQuery}`);
      console.log("Fetched issues response:", response.data);
      const issueList = Array.isArray(response.data) ? response.data : response.data.issues || [];
      setIssues(issueList);
    } catch (error) {
      console.error("Error fetching issues:", error);
      setIssues([]);
    }
  };

  const downloadIssue = async (issue) => {
    try {
      const zip = new JSZip();

      zip.file(`${issue.issueTitle}.txt`, 
        `Issue: ${issue.issueTitle}\n` +
        `Project: ${issue.projectName}\n` +
        `Description: ${issue.description}\n` +
        `Solution: ${issue.solution?.description || 'No solution yet'}`
      );

      if (issue.solution?.files && Array.isArray(issue.solution.files)) {
        for (const file of issue.solution.files) {
          const fileData = await fetch(file.url).then(res => res.blob());
          zip.file(file.name, fileData);
        }
      }

      const content = await zip.generateAsync({ type: "blob" });
      saveAs(content, `${issue.issueTitle}.zip`);
    } catch (error) {
      console.error("Error downloading issue:", error);
    }
  };

  return (
    <div className="p-4">
      <div className="flex items-center mb-4">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search issues..."
          className="border rounded px-4 py-2 mr-2 w-full max-w-md"
        />
        <button
          onClick={handleSearch}
          className="bg-blue-500 text-white px-4 py-2 rounded flex items-center"
        >
          <Search className="mr-2" /> Search
        </button>
      </div>

      <div className="grid gap-4">
        {Array.isArray(issues) && issues.map(issue => (
          <div key={issue._id}>
            <IssueCard issue={issue} />
            <button
              onClick={() => downloadIssue(issue)}
              className="mt-2 bg-green-500 text-white px-4 py-1 rounded flex items-center"
            >
              <Download className="mr-1" size={16} /> Download
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
