import React, { useState } from 'react';
import {
  Eye, EyeOff, Edit, Save, X,
  FileText, CheckCircle
} from 'lucide-react';
import axios from 'axios';
import EditSolutionModal from './EditSolutionModal';

export default function IssueCard({ issue, onUpdate }) {
  const [showDescription, setShowDescription] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editedIssue, setEditedIssue] = useState({ ...issue });
  const [showEditModal, setShowEditModal] = useState(false);

  const handleEdit = () => {
    setIsEditing(true);
    setEditedIssue({ ...issue });
  };

  const handleSave = async () => {
    try {
      await axios.patch(`/api/issues/${issue._id}`, editedIssue);
      onUpdate(); // Refresh the issues list
      setIsEditing(false);
    } catch (err) {
      console.error('Error updating issue:', err);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedIssue(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmitSolution = async (formData) => {
    try {
      const response = await axios.patch(`/api/issues/${issue._id}/solution`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setShowEditModal(false);
      onUpdate(); // Refresh the list
      return response.data;
    } catch (error) {
      console.error('Error submitting solution:', error);
      throw error;
    }
  };

  return (
    <div className="bg-white shadow overflow-hidden rounded-lg mb-4 border border-gray-200">
      <div className="px-4 py-5 sm:px-6 border-b flex justify-between items-start">
        <div>
          <div className="flex items-center">
            {isEditing ? (
              <input
                type="text"
                name="issueTitle"
                value={editedIssue.issueTitle}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md text-lg font-medium"
              />
            ) : (
              <h3 className="text-lg font-medium text-gray-900">{issue.issueTitle}</h3>
            )}
            {issue.solution && (
              <span className="ml-2 inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                <CheckCircle className="h-3 w-3 mr-1" />
                Solved
              </span>
            )}
          </div>
          <div className="mt-1 text-sm text-gray-500">
            Reported by: {issue.ntId} • {new Date(issue.createdAt).toLocaleDateString()}
          </div>
        </div>

        <div className="flex items-center space-x-2">
          <button
            onClick={() => setShowDescription(!showDescription)}
            className="p-1 text-gray-500 hover:text-gray-700"
          >
            {showDescription ? <EyeOff size={18} /> : <Eye size={18} />}
          </button>
          {!isEditing ? (
            <button onClick={handleEdit} className="p-1 text-blue-500 hover:text-blue-700">
              <Edit size={18} />
            </button>
          ) : (
            <>
              <button onClick={handleSave} className="p-1 text-green-500 hover:text-green-700">
                <Save size={18} />
              </button>
              <button onClick={() => setIsEditing(false)} className="p-1 text-red-500 hover:text-red-700">
                <X size={18} />
              </button>
            </>
          )}
        </div>
      </div>

      <div className="px-4 py-3 sm:px-6 text-sm text-gray-500 flex flex-wrap gap-x-4 gap-y-2">
        <div><span className="font-medium">Project:</span> {issue.projectName}</div>
        <div><span className="font-medium">Status:</span>
          <span className={`ml-1 px-2 py-1 text-xs rounded-full ${
            issue.status === 'Resolved' ? 'bg-green-100 text-green-800' :
            issue.status === 'In Progress' ? 'bg-yellow-100 text-yellow-800' :
            'bg-gray-100 text-gray-800'
          }`}>
            {issue.status}
          </span>
        </div>
      </div>

      {(showDescription || isEditing) && (
        <div className="border-t border-gray-200 px-4 py-4 sm:px-6">
          {isEditing ? (
            <textarea
              name="description"
              value={editedIssue.description}
              onChange={handleChange}
              rows={4}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
            />
          ) : (
            <p className="text-sm text-gray-700 whitespace-pre-line">{issue.description}</p>
          )}

          {/* Solution section */}
          {issue.status === 'Resolved' && issue.solution && (
            <div className="mt-4 p-3 bg-blue-50 rounded-md">
              <div className="flex justify-between mb-1">
                <h4 className="text-sm font-medium text-blue-800">Solution ({issue.solution.category})</h4>
                <div className="text-xs text-gray-500">
                  Solved by: {issue.solution.solvedBy?.ntId || 'N/A'} • {new Date(issue.solution.solvedAt).toLocaleDateString()}
                </div>
              </div>
              <p className="text-sm text-blue-800">{issue.solution.description}</p>

              {issue.solution.files?.length > 0 && (
                <div className="mt-2">
                  <h5 className="text-xs font-medium text-blue-700 mb-1">Attachments:</h5>
                  <div className="flex flex-wrap gap-2">
                    {issue.solution.files.map((file, index) => (
                      <a
                        key={index}
                        href={`/uploads/${file.filename}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-xs text-blue-600 hover:underline flex items-center"
                      >
                        <FileText className="inline h-3 w-3 mr-1" />
                        {file.originalname}
                      </a>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}

          {/* Show Add Solution button if not resolved */}
          {issue.status !== 'Resolved' && !issue.solution && (
            <button
              onClick={() => setShowEditModal(true)}
              className="mt-4 inline-flex items-center px-3 py-1.5 text-xs font-medium rounded-md bg-blue-100 text-blue-700 hover:bg-blue-200"
            >
              <Edit className="h-4 w-4 mr-1" />
              Add Solution
            </button>
          )}
        </div>
      )}

      {/* Add/Edit Solution Modal */}
      {showEditModal && (
        <EditSolutionModal
          issue={issue}
          onClose={() => setShowEditModal(false)}
          onSubmit={handleSubmitSolution}
        />
      )}
    </div>
  );
}
