import { useState } from 'react';
import { Search, Download, FileText } from 'lucide-react';
import axios from 'axios';

export default function Implementation() {
  const [searchQuery, setSearchQuery] = useState('');
  const [issues, setIssues] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const handleSearch = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get('/api/issues', {
        params: { search: searchQuery, status: 'Resolved' }
      });
      setIssues(response.data.data);
    } catch (error) {
      console.error('Search failed:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDownload = async (issueId) => {
    try {
      const response = await axios.get(`/api/issues/${issueId}/export`, {
        responseType: 'blob'
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `issue-${issueId}.zip`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Download failed:', error);
    }
  };

  return (
    <div className="max-w-6xl mx-auto p-4">
      <h1 className="text-2xl font-bold text-gray-800 mb-6">Implementation</h1>

      <div className="flex mb-6">
        <div className="relative flex-1">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <Search className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            placeholder="Search resolved issues..."
            className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          />
        </div>
        <button
          onClick={handleSearch}
          disabled={isLoading}
          className="ml-3 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 flex items-center"
        >
          {isLoading ? 'Searching...' : (
            <>
              <Search className="h-4 w-4 mr-2" />
              Search
            </>
          )}
        </button>
      </div>

      <div className="space-y-4">
        {isLoading ? (
          <div className="flex justify-center py-10">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
          </div>
        ) : issues.length > 0 ? (
          issues.map(issue => (
            <div key={issue._id} className="bg-white shadow rounded-lg overflow-hidden">
              <div className="px-4 py-3 flex justify-between items-center border-b">
                <div>
                  <h3 className="font-medium">{issue.issueTitle}</h3>
                  <p className="text-sm text-gray-600">{issue.projectName}</p>
                </div>
                <button
                  onClick={() => handleDownload(issue._id)}
                  className="px-3 py-1 bg-green-600 text-white rounded-md text-sm hover:bg-green-700 flex items-center"
                >
                  <Download className="h-4 w-4 mr-2" />
                  Download
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="text-center py-10 text-gray-500">
            {searchQuery ? 'No resolved issues found' : 'Search for resolved issues to download'}
          </div>
        )}
      </div>
    </div>
  );
}
