import React, { useState } from 'react';
import axios from 'axios';

const ReportIssueForm = () => {
  const [formData, setFormData] = useState({
    projectName: '',
    ratedPower: '',
    ratedSpeed: '',
    application: '',
    legislation: '',
    fieSystem: '',
    egtSystem: '',
    issueTitle: '',
    description: '',
    issueType: 'Hardware',
    files: []
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setFormData((prev) => ({ ...prev, files: e.target.files }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const submissionData = new FormData();

    Object.entries(formData).forEach(([key, value]) => {
      if (key === 'files') {
        Array.from(value).forEach(file => submissionData.append('files', file));
      } else {
        submissionData.append(key, value);
      }
    });

    try {
      console.log("Submitting to backend with data:", formData);
      await axios.post(
        'https://animated-lamp-xq555p57vqfpppq-5000.app.github.dev/api/issues',
        submissionData,
        {
          headers: { 'Content-Type': 'multipart/form-data' },
          withCredentials: true
        }
      );
      alert('Issue submitted successfully.');
    } catch (err) {
      console.error("Submission error:", err.response?.data || err.message || err);
      alert('Error submitting issue.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 p-4">
      <input type="text" name="projectName" value={formData.projectName} onChange={handleChange} placeholder="Project Name" className="input" required />
      <input type="number" name="ratedPower" value={formData.ratedPower} onChange={handleChange} placeholder="Rated Power" className="input" required />
      <input type="number" name="ratedSpeed" value={formData.ratedSpeed} onChange={handleChange} placeholder="Rated Speed" className="input" required />
      <input type="text" name="application" value={formData.application} onChange={handleChange} placeholder="Application" className="input" required />
      <input type="text" name="legislation" value={formData.legislation} onChange={handleChange} placeholder="Legislation" className="input" required />
      <input type="text" name="fieSystem" value={formData.fieSystem} onChange={handleChange} placeholder="FIE System" className="input" />
      <input type="text" name="egtSystem" value={formData.egtSystem} onChange={handleChange} placeholder="EGT System" className="input" />
      <input type="text" name="issueTitle" value={formData.issueTitle} onChange={handleChange} placeholder="Issue Title" className="input" required />
      <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Description" className="textarea" required />
      <select name="issueType" value={formData.issueType} onChange={handleChange} className="select">
        <option value="Hardware">Hardware</option>
        <option value="Software">Software</option>
      </select>
      <input type="file" multiple onChange={handleFileChange} className="file-input" />
      <button type="submit" className="btn">Submit Issue</button>
    </form>
  );
};

export default ReportIssueForm;
