import React, { useState } from 'react';
import { CheckCircle, FileText, HardDrive, Settings, GitPullRequest, Edit } from 'lucide-react';
import EditSolutionModal from './EditSolutionModal';
import axios from 'axios';

export default function IssueCard({ issue }) {
  const [showEditModal, setShowEditModal] = useState(false);

  const handleSubmitSolution = async (formData) => {
    try {
      const response = await axios.patch(`/api/issues/${issue._id}/solution`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      return response.data;
    } catch (error) {
      console.error('Error submitting solution:', error);
      throw error;
    }
  };

  return (
    <div className="bg-white shadow-md rounded-lg overflow-hidden mb-6 border border-gray-200 hover:shadow-lg transition-shadow duration-200">
      {/* Issue Header with Edit button */}
      <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-start">
        <div>
          <div className="flex items-center">
            <h3 className="text-xl font-semibold text-gray-800">{issue.issueTitle}</h3>
            {issue.solution && (
              <span className="ml-2 inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                <CheckCircle className="h-3 w-3 mr-1" />
                Solved
              </span>
            )}
          </div>
          <div className="mt-1 text-sm text-gray-500">
            Reported by: {issue.ntId} • {new Date(issue.createdAt).toLocaleDateString()}
          </div>
        </div>
        {!issue.solution && (
          <button
            onClick={() => setShowEditModal(true)}
            className="text-blue-600 hover:text-blue-800 flex items-center text-sm"
          >
            <Edit className="h-4 w-4 mr-1" />
            Add Solution
          </button>
        )}
      </div>

      {/* Issue Content */}
      <div className="px-6 py-4">
        <div className="mb-4">
          <h4 className="text-sm font-medium text-gray-500 mb-1">Description</h4>
          <p className="text-gray-700 whitespace-pre-line">{issue.description}</p>
        </div>

        {/* Solution Section */}
        {issue.solution && (
          <div className="mt-6 border-t pt-4">
            <div className="flex items-center justify-between mb-2">
              <h4 className="text-sm font-medium text-gray-500">
                Solution ({issue.solution.category})
              </h4>
              <div className="text-xs text-gray-500">
                Solved by: {issue.solution?.solvedBy?.ntId || 'N/A'} •{' '}
                {issue.solution?.solvedAt
                  ? new Date(issue.solution.solvedAt).toLocaleDateString()
                  : 'Unknown date'}
              </div>
            </div>
            <div className="bg-blue-50 p-3 rounded-md">
              <p className="text-gray-800">{issue.solution.description}</p>

              {issue.solution.files?.length > 0 && (
                <div className="mt-3">
                  <h5 className="text-xs font-medium text-gray-500 mb-1">Supporting Files:</h5>
                  <div className="flex flex-wrap gap-2">
                    {issue.solution.files.map((file, index) => (
                      <a
                        key={index}
                        href={`/uploads/${file.filename}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="inline-flex items-center px-2 py-1 rounded text-xs font-medium bg-white text-blue-600 hover:bg-blue-50"
                      >
                        <FileText className="h-3 w-3 mr-1" />
                        {file.originalname}
                      </a>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Edit Solution Modal */}
      {showEditModal && (
        <EditSolutionModal
          issue={issue}
          onClose={() => setShowEditModal(false)}
          onSubmit={handleSubmitSolution}
        />
      )}
    </div>
  );
}
