import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useState } from 'react';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import SearchIssues from './pages/SearchIssues';
import ReportIssueForm from './pages/ReportIssueForm';
import BoschLogo from './components/BoschLogo';
import '@fontsource/inter';



function App() {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        {/* Navigation */}
        <Navbar sidebarOpen={sidebarOpen} setSidebarOpen={setSidebarOpen} />
        <Sidebar sidebarOpen={sidebarOpen} setSidebarOpen={setSidebarOpen} />

        {/* Main Content */}
        <main className="pt-16 pl-0 md:pl-64">
          {/* Bosch Branded Header */}
          <div className="bg-white shadow-sm border-b border-bosch-gray">
            <div className="max-w-7xl mx-auto py-4 px-4 sm:px-6 lg:px-8">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <BoschLogo />
                </div>
                <div className="ml-4 border-l border-bosch-gray pl-4">
                  <h1 className="text-xl font-bold text-bosch-dark">
                    Engineering Lessons Learned
                  </h1>
                  <p className="text-sm text-bosch-blue">
                    Knowledge sharing platform
                  </p>
                </div>
              </div>
            </div>
          </div>

          {/* Page Content */}
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
            <Routes>
              <Route path="/" element={<SearchIssues />} />
              <Route path="/report" element={<ReportIssueForm />} />
            </Routes>
          </div>
        </main>
      </div>
    </Router>
  );
}

export default App;
