import React, { useState } from 'react';
import { Eye, EyeOff, Edit, Save, X, FileText, HardDrive, Settings, GitPullRequest } from 'lucide-react';
//import React from 'react';
import axios from 'axios';

export default function IssueCard({ issue, onUpdate }) {
  const [showDescription, setShowDescription] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editedIssue, setEditedIssue] = useState({...issue});

  const handleEdit = () => {
    setIsEditing(true);
    setEditedIssue({...issue});
  };

  const handleSave = async () => {
    try {
      await axios.patch(`/api/issues/${issue._id}`, editedIssue);
      onUpdate(); // Refresh the issues list
      setIsEditing(false);
    } catch (err) {
      console.error('Error updating issue:', err);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedIssue(prev => ({ ...prev, [name]: value }));
  };

  return (
    <div className="bg-white shadow overflow-hidden rounded-lg mb-4">
      <div className="px-4 py-5 sm:px-6">
        <div className="flex items-center justify-between">
          {isEditing ? (
            <input
              type="text"
              name="issueTitle"
              value={editedIssue.issueTitle}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
            />
          ) : (
            <h3 className="text-lg font-medium text-gray-900">{issue.issueTitle}</h3>
          )}
          <div className="flex items-center space-x-2">
            <button
              onClick={() => setShowDescription(!showDescription)}
              className="p-1 text-gray-500 hover:text-gray-700"
            >
              {showDescription ? <EyeOff size={18} /> : <Eye size={18} />}
            </button>
            {!isEditing ? (
              <button onClick={handleEdit} className="p-1 text-blue-500 hover:text-blue-700">
                <Edit size={18} />
              </button>
            ) : (
              <>
                <button onClick={handleSave} className="p-1 text-green-500 hover:text-green-700">
                  <Save size={18} />
                </button>
                <button onClick={() => setIsEditing(false)} className="p-1 text-red-500 hover:text-red-700">
                  <X size={18} />
                </button>
              </>
            )}
          </div>
        </div>
        <div className="mt-1 flex flex-wrap gap-x-4 gap-y-2 text-sm text-gray-500">
          <div><span className="font-medium">Project:</span> {issue.projectName}</div>
          <div><span className="font-medium">Status:</span> 
            <span className={`ml-1 px-2 py-1 text-xs rounded-full ${
              issue.status === 'Resolved' ? 'bg-green-100 text-green-800' : 
              issue.status === 'In Progress' ? 'bg-yellow-100 text-yellow-800' : 
              'bg-gray-100 text-gray-800'
            }`}>
              {issue.status}
            </span>
          </div>
        </div>
      </div>

      {(showDescription || isEditing) && (
        <div className="border-t border-gray-200 px-4 py-4 sm:px-6">
          {isEditing ? (
            <textarea
              name="description"
              value={editedIssue.description}
              onChange={handleChange}
              rows={4}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
            />
          ) : (
            <p className="text-sm text-gray-700">{issue.description}</p>
          )}

          {/* Solution section for resolved issues */}
          {issue.status === 'Resolved' && issue.solution && (
            <div className="mt-4 p-3 bg-green-50 rounded-md">
              <h4 className="text-sm font-medium text-green-800 mb-1">Solution</h4>
              <p className="text-sm text-green-700">{issue.solution.description}</p>
              {issue.solution.files?.length > 0 && (
                <div className="mt-2">
                  <h5 className="text-xs font-medium text-green-700 mb-1">Attachments:</h5>
                  <div className="flex flex-wrap gap-2">
                    {issue.solution.files.map((file, index) => (
                      <a
                        key={index}
                        href={`/uploads/${file.filename}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-xs text-green-600 hover:underline"
                      >
                        <FileText className="inline h-3 w-3 mr-1" />
                        {file.originalname}
                      </a>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}
