const handleSubmit = async (e) => {
  e.preventDefault();
  setIsSubmitting(true);

  try {
    const formData = new FormData();
    // Append all form fields
    Object.entries(formData).forEach(([key, value]) => {
      formData.append(key, value);
    });

    // Append files
    files.forEach(file => {
      formData.append('files', file);
    });

    const response = await axios.post('http://localhost:5000/api/issues', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
      timeout: 8000 // 8 second timeout
    });

    if (response.status === 201) {
      alert('Issue submitted successfully!');
      // Reset form
    }
  } catch (err) {
    if (err.response) {
      // Server responded with error status
      console.error('Server error:', err.response.data);
      alert(`Error: ${err.response.data.message || err.response.statusText}`);
    } else if (err.request) {
      // Request was made but no response
      console.error('No response received:', err.request);
      alert('Server is not responding. Please check:\n1. Backend is running\n2. Network connection');
    } else {
      // Other errors
      console.error('Error:', err.message);
      alert(`Error: ${err.message}`);
    }
  } finally {
    setIsSubmitting(false);
  }
};
