import React, { useState } from 'react';
import IssueCard from '../components/IssueCard';
import { Search } from 'lucide-react';

export default function SearchIssues() {
  const [searchQuery, setSearchQuery] = useState('');
  const [issues, setIssues] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const handleSearch = async () => {
    setIsLoading(true);
    try {
      const res = await fetch(`http://localhost:5000/api/issues/search?search=${encodeURIComponent(searchQuery)}`);
      const json = await res.json();
      setIssues(json.data); // Use 'data' because backend sends { success, count, data }
    } catch (error) {
      console.error('Search failed:', error);
      alert('Search failed. Check server connection.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6">Search Issues</h1>
      <div className="flex mb-6">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search issues..."
          className="flex-1 p-2 border rounded-l"
        />
        <button
          onClick={handleSearch}
          disabled={isLoading}
          className="bg-blue-500 text-white p-2 rounded-r flex items-center"
        >
          <Search className="h-5 w-5 mr-1" />
          Search
        </button>
      </div>

      {isLoading ? (
        <div>Loading...</div>
      ) : (
        <div className="space-y-4">
          {issues.length > 0 ? (
            issues.map(issue => (
              <IssueCard key={issue._id} issue={issue} />
            ))
          ) : (
            <p>No issues found.</p>
          )}
        </div>
      )}
    </div>
  );
}
