import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FilePlus, Upload, X } from 'lucide-react';

const initialFormState = {
  projectName: '',
  ratedPower: '',
  ratedSpeed: '',
  application: '',
  legislation: '',
  fieSystem: '',
  egtSystem: '',
  issueTitle: '',
  description: '',
  issueType: 'Hardware',
};

export default function ReportIssueForm() {
  const [formData, setFormData] = useState(initialFormState);
  const [files, setFiles] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const newFiles = Array.from(e.target.files);
    setFiles(prev => [...prev, ...newFiles]);
  };

  const removeFile = (index) => {
    setFiles(prev => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');

    try {
      const data = new FormData();

      // Append form fields
      Object.entries(formData).forEach(([key, value]) => {
        data.append(key, value);
      });

      // Append files
      files.forEach(file => {
        data.append('files', file);
      });

      const response = await axios.post('http://localhost:5000/api/issues', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.success) {
        // Reset form on success
        setFormData(initialFormState);
        setFiles([]);
        alert('Issue submitted successfully!');
        navigate('/'); // Redirect after success
      } else {
        throw new Error(response.data.message || 'Submission failed');
      }
    } catch (err) {
      let errorMessage = 'Failed to submit issue';

      if (err.response) {
        console.error('Server error:', err.response.data);
        errorMessage = err.response.data.message ||
          err.response.data.error ||
          `Server error (${err.response.status})`;

        if (err.response.data.missingFields) {
          errorMessage += `\nMissing fields: ${err.response.data.missingFields.join(', ')}`;
        }
      } else if (err.request) {
        console.error('No response:', err.request);
        errorMessage = 'No response from server. Is the backend running?';
      } else {
        console.error('Error:', err.message);
        errorMessage = err.message;
      }

      setError(errorMessage);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h1 className="text-xl font-bold mb-4">Report Issue</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        {Object.entries(initialFormState).map(([key, _]) => (
          <input
            key={key}
            name={key}
            value={formData[key]}
            onChange={handleChange}
            placeholder={key}
            className="border p-2 w-full"
            required={key !== 'description'} // Make description optional
          />
        ))}
        <input
          type="file"
          multiple
          onChange={handleFileChange}
          className="border p-2 w-full"
        />
        <div className="flex flex-wrap gap-2">
          {files.map((file, index) => (
            <div key={index} className="flex items-center space-x-1">
              <span>{file.name}</span>
              <button type="button" onClick={() => removeFile(index)}>
                <X size={16} />
              </button>
            </div>
          ))}
        </div>
        {error && <div className="text-red-500">{error}</div>}
        <button
          type="submit"
          disabled={isSubmitting}
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          {isSubmitting ? 'Submitting...' : 'Submit Issue'}
        </button>
      </form>
    </div>
  );
}
