import React, { useState } from 'react';
import axios from 'axios';
import { Eye, Edit, Download } from 'lucide-react';
import SolutionModal from './SolutionModal';

export default function SearchIssues() {
  const [searchQuery, setSearchQuery] = useState('');
  const [issues, setIssues] = useState([]);
  const [selectedIssue, setSelectedIssue] = useState(null);
  const [showSolutionModal, setShowSolutionModal] = useState(false);

  const handleSearch = async () => {
    const response = await axios.get(`/api/issues?search=${searchQuery}`);
    setIssues(response.data);
  };

  const handleAddSolution = (issue) => {
    setSelectedIssue(issue);
    setShowSolutionModal(true);
  };

  return (
    <div className="max-w-6xl mx-auto p-4">
      <div className="flex mb-6">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search issues..."
          className="flex-1 p-2 border rounded-l"
        />
        <button
          onClick={handleSearch}
          className="bg-blue-500 text-white p-2 rounded-r"
        >
          Search
        </button>
      </div>

      <div className="space-y-4">
        {issues.map(issue => (
          <div key={issue._id} className="bg-white shadow rounded-lg p-4">
            <div className="flex justify-between items-center">
              <div>
                <h3 className="font-bold">{issue.issueTitle}</h3>
                <p className="text-sm text-gray-600">{issue.application}</p>
                {issue.solution && (
                  <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded">
                    Solved
                  </span>
                )}
              </div>
              <div className="flex space-x-2">
                <button 
                  onClick={() => setSelectedIssue(issue)}
                  className="text-blue-500 hover:text-blue-700"
                >
                  <Eye size={18} />
                </button>
                <button
                  onClick={() => handleAddSolution(issue)}
                  className="text-yellow-500 hover:text-yellow-700"
                >
                  <Edit size={18} />
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {selectedIssue && (
        <IssueDetailModal 
          issue={selectedIssue}
          onClose={() => setSelectedIssue(null)}
        />
      )}

      {showSolutionModal && (
        <SolutionModal
          issue={selectedIssue}
          onClose={() => setShowSolutionModal(false)}
          onSubmit={async (solutionData) => {
            await axios.post(`/api/issues/${selectedIssue._id}/solutions`, solutionData);
            handleSearch(); // Refresh the list
          }}
        />
      )}
    </div>
  );
}
